  },

];

const useStyles = makeStyles({
  root: {
    width: '100%',
    height: '89vh',

  },
  container: {
    maxHeight: 800,
  },
  button: {
    margin: '10px',
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 'auto',
  },
});

const ContactList = ({ contacts, getContacts }) => {
  const classes = useStyles();
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(15);
  const [showComments, setShowComments] = useState(false);
  const [searchValue, setSearchValue] = useState(null);

  useEffect(() => {
    getContacts({ uuid: '2196e5d6-8b45-4b28-98c6-2aa34618d7b8' });
    console.log('contacts in list component', contacts);
  }, [contacts]);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const handleComment = (event) => {
    console.log('comment event', event);
    setShowComments(true);
  };

  const handleClose = () => {
    setShowComments(false);
  };

  const items = contactsList.filter((result) => {
    if (searchValue === null) {
      return result;
    }
    return result.name.toLowerCase().includes(searchValue.toLowerCase()) || result.email.toLowerCase().includes(searchValue.toLowerCase()) || result.phoneNo.includes(searchValue);
  }).slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((result) => {
    console.log('hii');
    return (
      <TableRow hover role="checkbox" tabIndex={-1}>
        <TableCell scope="row" align="center">
          <Typography style={{ display: 'inline-block' }} color="textSecondary">
            {result.name}
          </Typography>
        </TableCell>
        <TableCell scope="row" align="center">
          <Typography style={{ display: 'inline-block' }} color="textSecondary">
            {result.email}
          </Typography>
        </TableCell>
        <TableCell scope="row" align="center">
          <Typography style={{ display: 'inline-block' }} color="textSecondary">
            {result.phoneNo}
          </Typography>
        </TableCell>
        <TableCell scope="row" align="center">
          <Typography style={{ display: 'inline-block' }} color="textSecondary">
            {result.title}
          </Typography>
        </TableCell>
      </TableRow>
    );
  });

  return (
    <>
      <Paper className={classes.root}>
        <TableContainer className={classes.container}>
          <Table stickyHeader aria-label="sticky table">
            <TableHead style={{ display: 'table-header-group' }}>
              <TableRow>
                <TableCell align="center" style={{ width: '50px' }}>Name</TableCell>
                <TableCell align="center" style={{ minWidth: '50px' }}>Email</TableCell>
                <TableCell align="center" style={{ minWidth: '50px' }}>Phone Number</TableCell>
                <TableCell align="center" style={{ minWidth: '50px' }}>Title</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {items}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[10, 15, 25, 100]}
          component="div"
          count={contactsList.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
      </Paper>
      <div>
        {showComments && (
          <Modal
            aria-labelledby="transition-modal-title"
            aria-describedby="transition-modal-description"
            className={classes.modal}
            open={showComments}
            onClose={handleClose}
            closeAfterTransition
            BackdropComponent={Backdrop}
            BackdropProps={{
              timeout: 500,
            }}
          >
            <Fade in={showComments}>
              <div className={classes.paper}>
                <h2 id="transition-modal-title">showing list of comments</h2>
                <Button
                  variant="contained"
                  color="primary"
                  size="small"
                  className={classes.button}
                  onClick={() => { handleComment(); }}
                >
                  Add Comment
                </Button>
              </div>
            </Fade>
          </Modal>
        )}
      </div>
    </>
  );
};

// const mapStateToProps = (state) => ({ contacts: state.contacts });

// export default connect(mapStateToProps)(ContactList);

export default connect(
  ({ contacts }) => ({
    contacts,
  }),
  {
    getContacts: actions.getContacts,
  },
)(ContactList);
