/* eslint-disable no-unused-vars */
import { call, put, takeLatest } from 'redux-saga/effects';
import {
  LOGOUT, GET_ACCOUNTS,
} from '../Constants/login';
import { setProfile, logout } from '../Actions/login';
import { getAccounts, getContacts, logoutUser } from '../../Api';

export function* getLoginAccounts() {
  const response = yield call(getAccounts, '/users/login');
  yield put(setProfile(response));
}

export function* logoutApi() {
  const response = yield call(logoutUser, '/users/logout');
  yield put(logout);
}

export function* watchLoginApi() {
  yield takeLatest(GET_ACCOUNTS, getLoginAccounts);
  yield takeLatest(LOGOUT, logoutApi);
}
