import {
  LOGIN_SUCCESS, LOGIN_FAIL, LOGOUT, CLEAR_STORE,
} from '../Constants/login';

const initialState = {
  profile: {},
};

const profile = (state = initialState.profile, { type, value }) => {
  console.log('Type and value', type, value);
  switch (type) {
    case LOGIN_SUCCESS:
      return value;

    case LOGIN_FAIL:
      return 'Login failed';

    case LOGOUT:
    case CLEAR_STORE:
      return initialState.profile;

    default:
      return state;
  }
};

export default profile;
