/* eslint-disable no-console */
import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const columns = [
  { id: 'name', label: 'Name', minWidth: 150 },
  { id: 'email', label: 'Email', minWidth: 150 },
  {
    id: 'phoneNumber',
    label: 'Phone Number',
    minWidth: 150,
    align: 'right',
  },
  {
    id: 'title',
    label: 'Company/Title',
    minWidth: 150,
    align: 'right',
  },
  {
    id: 'label',
    label: 'Labels',
    minWidth: 150,
    align: 'right',
  },
  {
    id: 'blank',
    label: '',
    minWidth: 150,
    align: 'right',
  },
];

function createData(name, email, phoneNumber, title, label) {
  return {
    name, email, phoneNumber, title, label,
  };
}

const rows = [
  createData('avni tanwar', 'avnitanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('vansh', 'vansh@gmail.com', '1324171354', 'SE', 'Any'),
  createData('nitya tanwar', 'nityatanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('avni tanwar', 'avnitanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('vansh', 'vansh@gmail.com', '1324171354', 'SE', 'Any'),
  createData('nitya tanwar', 'nityatanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('avni tanwar', 'avnitanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('vansh', 'vansh@gmail.com', '1324171354', 'SE', 'Any'),
  createData('nitya tanwar', 'nityatanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('avni tanwar', 'avnitanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('vansh', 'vansh@gmail.com', '1324171354', 'SE', 'Any'),
  createData('nitya tanwar', 'nityatanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('avni tanwar', 'avnitanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('vansh', 'vansh@gmail.com', '1324171354', 'SE', 'Any'),
  createData('nitya tanwar', 'nityatanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('avni tanwar', 'avnitanwar@gmail.com', '1324171354', 'SE', 'Any'),
  createData('vansh', 'vansh@gmail.com', '1324171354', 'SE', 'Any'),
  createData('nitya tanwar', 'nityatanwar@gmail.com', '1324171354', 'SE', 'Any'),

];

const useStyles = makeStyles({
  root: {
    width: '100%',
    height: '89vh',

  },
  container: {
    maxHeight: 800,
  },
  button: {
    margin: '10px',
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 'auto',
  },
});

const ContactList = () => {
  const classes = useStyles();
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(15);
  const [showComments, setShowComments] = useState(false);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const handleComment = (event) => {
    console.log('comment event', event);
    setShowComments(true);
  };

  const handleClose = () => {
    setShowComments(false);
  };

  /* const applyFilters = () => (
    console.log('applying filters')
  ); */

  return (
    <>
      <Paper className={classes.root}>
        <TableContainer className={classes.container}>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                {columns.map((column) => (
                  <TableCell
                    key={column.id}
                    align={column.align}
                    style={{ minWidth: column.minWidth }}
                  >
                    {column.label}
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                  {columns.map((column) => {
                    const value = row[column.id];
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                    );
                  })}
                  <Button
                    variant="contained"
                    color="primary"
                    size="small"
                    className={classes.button}
                    onClick={() => { handleComment(); }}
                  >
                    Comment
                  </Button>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[10, 15, 25, 100]}
          component="div"
          count={rows.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
      </Paper>
      <div>
        {showComments && (
          <Modal
            aria-labelledby="transition-modal-title"
            aria-describedby="transition-modal-description"
            className={classes.modal}
            open={showComments}
            onClose={handleClose}
            closeAfterTransition
            BackdropComponent={Backdrop}
            BackdropProps={{
              timeout: 500,
            }}
          >
            <Fade in={showComments}>
              <div className={classes.paper}>
                <h2 id="transition-modal-title">showing list of comments</h2>
                <Button
                  variant="contained"
                  color="primary"
                  size="small"
                  className={classes.button}
                  onClick={() => { handleComment(); }}
                >
                  Add Comment
                </Button>
              </div>
            </Fade>
          </Modal>
        )}
      </div>
    </>
  );
};

export default ContactList;
